          <br /><span class="description white-text">{project.description}</span>
          <br /><span class="date grey-text">Latest commit: <Time relative timestamp={new Date(project.pushed_at)} /> &#8226; {project.stargazers_count} Stars</span>
        </div>
      </div>
    {/if}
    
    <ul class="tabs" style="background: #333;">
      <li class="tab col s3"><a class="active" href="#readme">README</a></li>
      <li class="tab col s3"><a href="#built-with">Built With</a></li>
    </ul>
    <div id="readme" class="card-content" style="overflow: auto;">
      <SvelteMarkdown {source} />
    </div>
    <div id="built-with" class="card-content">
      <h1 class="title">Applications</h1>
      <div class="description">
        Made with &hearts; by isTrav:
      </div>
      <br />
      <AppList selected={selectedApp} apps={builtWith} />
    </div>
  </div>
  <a href="/projects" class="btn btn-large red lighten-2">‚Üê Back to showcase</a>
</div>

